import React from 'react';
import { toast } from 'react-hot-toast';
import CloseIcon from '@mui/icons-material/Close';

const CustomErrorToast = (errorMessage) => {
  console.log(errorMessage,"errorMessage in toast")
  return toast(
    (t) => (
      <div className="toaster-css  rounded-[4px]  border-1 border-[#e93c3c] divide-solid p-[10px] bg-[#ffe4e6] size-full h-full">
        <div className="toaster-error flex items-center justify-between decoration-[#e93c3c] text-[16px]">
          <div className="flex-propertie-wi-jc">
            {/* <img
              src={alertTringle}
              alt=""
              className="error-tri"
            /> */}
            Error{' '}
          </div>
          <CloseIcon
            className="error-toat-close-icon"
            onClick={() => toast.remove(t.id)}
          />{' '}
        </div>
        <div>{errorMessage}</div>
      </div>
    ),
    { className: 'main-toaster-class' },
  );
};

export default CustomErrorToast;
