import axios from "axios";

const Config = axios.create({
    baseURL: ``
})
Config.interceptors.response.use(
    (response) => {
       return response;
    },
    (error)=>{
        const updatedMessage = error?.response?.data?.data[0];
        CustomErrorToast(updatedMessage);
    }

);

export default Config;
