import { createSlice } from "@reduxjs/toolkit";

export const productSlice=createSlice({
    name:'product',
    initialState:{
        products: [],
        brands: [],
        categories: [],
        status: 'idle',
        totalItems: 0,
        selectedProduct: null,
    },
    reducers:{},
    extraReducers:(builder)=>{}
});

export default productSlice.reducer;
