import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { addToCart,fetchItemsByUserId,updateCart,deleteItemFromCart,resetCart } from "../services/cartService";

export const addToCartAsync = createAsyncThunk(
    'cart/addToCart',
    async ({item, alert}) => {
      const response = await addToCart(item);
      alert.success('Item Added to Cart');

      // The value we return becomes the `fulfilled` action payload
      return response.data;
    }
);

export const fetchItemsByUserIdAsync = createAsyncThunk(
    'cart/fetchItemsByUserId',
    async () => {
      const response = await fetchItemsByUserId();
      // The value we return becomes the `fulfilled` action payload
      return response.data;
    }
);

export const updateCartAsync = createAsyncThunk(
    'cart/updateCart',
    async (update) => {
      const response = await updateCart(update);
      // The value we return becomes the `fulfilled` action payload
      return response.data;
    }
);

export const deleteItemFromCartAsync = createAsyncThunk(
    'cart/deleteItemFromCart',
    async (itemId) => {
      const response = await deleteItemFromCart(itemId);
      // The value we return becomes the `fulfilled` action payload
      return response.data;
    }
);

export const resetCartAsync = createAsyncThunk(
    'cart/resetCart',
    async () => {
      const response = await resetCart();
      // The value we return becomes the `fulfilled` action payload
      return response.data;
    }
);

export const cartSlice=createSlice({
    name:'cart',
    initialState:{
        status: '',
        items: [],
        cartLoaded: false
    },
    reducers:{},
    extraReducers:(builder)=>{
        builder.addCase(addToCartAsync.pending, (state) => { })
        .addCase(addToCartAsync.fulfilled, (state) => { })
        .addCase(addToCartAsync.rejected, (state) => { })

        .addCase(fetchItemsByUserIdAsync.pending, (state) => { })
        .addCase(fetchItemsByUserIdAsync.fulfilled, (state) => { })
        .addCase(fetchItemsByUserIdAsync.rejected, (state) => { })

        .addCase(updateCartAsync.pending, (state) => { })
        .addCase(updateCartAsync.fulfilled, (state) => { })
        .addCase(updateCartAsync.rejected, (state) => { })

        .addCase(deleteItemFromCartAsync.pending, (state) => { })
        .addCase(deleteItemFromCartAsync.fulfilled, (state) => { })
        .addCase(deleteItemFromCartAsync.rejected, (state) => { })

        .addCase(resetCartAsync.pending, (state) => { })
        .addCase(resetCartAsync.fulfilled, (state) => { })
        .addCase(resetCartAsync.rejected, (state) => { })
    }

})

export default cartSlice.reducer;
