import Config from "./index";

export const loginUser = async (loginInfo) => {
    return Config.post(`/auth/login`,loginInfo);
};

export const createUser = async (newUserInfo) => {
    return Config.post(`/auth/signup`,newUserInfo);
};

export const checkAuth=async()=>{
    return Config.get(`/auth/check`);
};

export const resetPasswordRequest = async (newUserInfo) => {
    return Config.post(`/auth/reset-password-request`,newUserInfo);
};
