import { useDispatch, useSelector } from 'react-redux';
import { clearSelectedProduct, createProductAsync, fetchProductByIdAsync, updateProductAsync,
} from '../../redux/productSlice';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { useAlert } from 'react-alert';
import Modal from '../resuablecomponent/Modal';
import * as Yup from 'yup';
import { useFormik } from 'formik';

export default function ProductionFrom() {


  const validationSchema = Yup.object().shape({
    title: Yup.string().required('Title is required'),
    description: Yup.string().required('Description is required'),
    price:Yup.number().required('Price is required'),
    discountPercentage:Yup.number().required('DiscountPercentage is required'),
    thumbnail:Yup.string().required('Thumbnail is required'),
    stock:Yup.string().required('stock is required'),
      image1:Yup.string().required('Email is required'),
      image2:Yup.string().required('Email is required'),
      image3:Yup.string().required('Email is required'),
      brand:Yup.string().required('Email is required'),
      category:Yup.string().required('Email is required'),
      highlight1:Yup.string().required('Email is required'),
      highlight2:Yup.string().required('Email is required'),
      highlight3:Yup.string().required('Email is required'),
      highlight4:Yup.string().required('Email is required'),
      sizes:[],
      colors:[]
});




const formik = useFormik({
    initialValues: {
      title: '',
      description: '',
      price:null,
      discountPercentage:null,
      thumbnail:'',
      stock:null,
      image1:'',
      image2:'',
      image3:'',
      brand:'',
      category:'',
      highlight1:'',
      highlight2:'',
      highlight3:'',
      highlight4:'',
      sizes:[],
      colors:[]
    },
    validationSchema,
    onSubmit: async(values) => {

    }
})
  const brands = useSelector((state) => state.product.brands);
  const categories = useSelector((state) => state.product.categories);
  const dispatch = useDispatch();
  const params = useParams();

  const selectedProduct = useSelector((state) => state.product.selectedProduct);
  console.log(params,"paramsid");
  const [openModal, setOpenModal] = useState(null);
 // const alert = useAlert();


  const colors = [
    {
      name: 'White',
      class: 'bg-white',
      selectedClass: 'ring-gray-400',
      id: 'white',
    },
    {
      name: 'Gray',
      class: 'bg-gray-200',
      selectedClass: 'ring-gray-400',
      id: 'gray',
    },
    {
      name: 'Black',
      class: 'bg-gray-900',
      selectedClass: 'ring-gray-900',
      id: 'black',
    },
  ];

  const sizes = [
    { name: 'XXS', inStock: true, id: 'xxs' },
    { name: 'XS', inStock: true, id: 'xs' },
    { name: 'S', inStock: true, id: 's' },
    { name: 'M', inStock: true, id: 'm' },
    { name: 'L', inStock: true, id: 'l' },
    { name: 'XL', inStock: true, id: 'xl' },
    { name: '2XL', inStock: true, id: '2xl' },
    { name: '3XL', inStock: true, id: '3xl' },
  ];

  useEffect(() => {
    if (params.id) {
      dispatch(fetchProductByIdAsync(params.id));
    } else {
      dispatch(clearSelectedProduct());
    }
  }, [params.id, dispatch]);

  //useEffect(() => {
  //  if (selectedProduct && params.id) {
      // setValue('title', selectedProduct.title);
      // setValue('description', selectedProduct.description);
      // setValue('price', selectedProduct.price);
      // setValue('discountPercentage', selectedProduct.discountPercentage);
      // setValue('thumbnail', selectedProduct.thumbnail);
      // setValue('stock', selectedProduct.stock);
      // setValue('image1', selectedProduct.images[0]);
      // setValue('image2', selectedProduct.images[1]);
      // setValue('image3', selectedProduct.images[2]);
      // setValue('brand', selectedProduct.brand);
      // setValue('category', selectedProduct.category);
      // setValue('highlight1', selectedProduct.highlights[0]);
      // setValue('highlight2', selectedProduct.highlights[1]);
      // setValue('highlight3', selectedProduct.highlights[2]);
      // setValue('highlight4', selectedProduct.highlights[3]);
      // setValue(
      //   'sizes',
      //   selectedProduct.sizes.map((size) => size.id)
      // );
      // setValue(
      //   'colors',
      //   selectedProduct.colors.map((color) => color.id)
      // );
  //   }
  // }, [selectedProduct, params.id, setValue]);

  const handleDelete = () => {
   // const product = { ...selectedProduct };
    //product.deleted = true;
    //dispatch(updateProductAsync(product));
  };

  return (
    <>
    <form
      noValidate
      // onSubmit={handleSubmit((data) => {
      //   console.log(data);
      //   const product = { ...data };
      //   product.images = [
      //     product.image1,
      //     product.image2,
      //     product.image3,
      //     product.thumbnail,
      //   ];
      //   product.highlights = [
      //     product.highlight1,
      //     product.highlight2,
      //     product.highlight3,
      //     product.highlight4,
      //   ];
      //   product.rating = 0;
      //   if (product.colors) {
      //     product.colors = product.colors.map((color) =>
      //       colors.find((clr) => clr.id === color)
      //     );
      //   }
      //   if (product.sizes) {
      //     product.sizes = product.sizes.map((size) =>
      //       sizes.find((sz) => sz.id === size)
      //     );
      //   }

      //   delete product['image1'];
      //   delete product['image2'];
      //   delete product['image3'];
      //   product.price = +product.price;
      //   product.stock = +product.stock;
      //   product.discountPercentage = +product.discountPercentage;
      //   console.log(product);
      //   if (params.id) {
      //     product.id = params.id;
      //     product.rating = selectedProduct.rating || 0;
      //     dispatch(updateProductAsync(product));
      //     alert.success('Product Updated');

      //     reset();
      //   } else {
      //     dispatch(createProductAsync(product));
      //     alert.success('Product Created');
      //     reset();
      //   }
      // })}
    >
      <div className="space-y-12 bg-white p-12">
        <div className="border-b border-gray-900/10 pb-12">
          <h2 className="text-base font-semibold leading-7 text-gray-900">
            Add Product
          </h2>

          <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
            {/* {selectedProduct && selectedProduct.deleted && (
              <h2 className="text-red-500 sm:col-span-6">
                This product is deleted
              </h2>
            )} */}

            <div className="sm:col-span-6">
              <label
                htmlFor="title"
                className="block text-sm font-medium leading-6 text-gray-900"
              >
                Product Name
              </label>
              <div className="mt-2">
                <div className="flex rounded-md shadow-sm ring-1 ring-inset ring-gray-300 focus-within:ring-2 focus-within:ring-inset focus-within:ring-indigo-600 ">
                  <input
                    type="text"
                    // {...register('title', {
                    //   required: 'name is required',
                    // })}
                    id="title"
                    className="block flex-1 border-0 bg-transparent py-1.5 pl-1 text-gray-900 placeholder:text-gray-400 focus:ring-0 sm:text-sm sm:leading-6"
                  />
                </div>
              </div>
            </div>

            <div className="col-span-full">
              <label
                htmlFor="description"
                className="block text-sm font-medium leading-6 text-gray-900"
              >
                Description
              </label>
              <div className="mt-2">
                <textarea
                  id="description"
                  // {...register('description', {
                  //   required: 'description is required',
                  // })}
                  rows={3}
                  className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                  defaultValue={''}
                />
              </div>
              <p className="mt-3 text-sm leading-6 text-gray-600">
                Write a few sentences about product.
              </p>
            </div>

            <div className="col-span-full">
              <label
                htmlFor="brand"
                className="block text-sm font-medium leading-6 text-gray-900"
              >
                Brand
              </label>
              <div className="mt-2">
                <select
                  // {...register('brand', {
                  //   required: 'brand is required',
                  // })}
                >
