import { useDispatch, useSelector } from 'react-redux';
import { clearSelectedProduct, createProductAsync, fetchProductByIdAsync, updateProductAsync,
} from '../../redux/productSlice';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { useAlert } from 'react-alert';
import Modal from '../resuablecomponent/Modal';
import * as Yup from 'yup';
import { useFormik } from 'formik';

export default function ProductionFrom() {


  const validationSchema = Yup.object().shape({
    title: Yup.string().required('Title is required'),
    description: Yup.string().required('Description is required'),
    price:Yup.number().required('Price is required'),
    discountPercentage:Yup.number().required('DiscountPercentage is required'),
    thumbnail:Yup.string().required('Thumbnail is required'),
    stock:Yup.number().required('Stock is required'),
    image1:Yup.string().required('Image1 is required'),
    image2:Yup.string().required('Image2 is required'),
    image3:Yup.string().required('Image3 is required'),
    brand:Yup.string().required('Brand is required'),
    category:Yup.string().required('Category is required'),
    highlight1:Yup.string().required('Highlight1 is required'),
    highlight2:Yup.string().required('Highlight2 is required'),
    highlight3:Yup.string().required('Highlight3 is required'),
    highlight4:Yup.string().required('highlight4 is required'),
    sizes:Yup.array().min(1, 'At least one size is required'),
    colors: Yup.array().min(1, 'At least one color is required')
});




const formik = useFormik({
    initialValues: {
      title: '',
      description: '',
      price:null,
      discountPercentage:null,
      thumbnail:'',
      stock:null,
      image1:'',
      image2:'',
      image3:'',
      brand:'',
      category:'',
      highlight1:'',
      highlight2:'',
      highlight3:'',
      highlight4:'',
      sizes:[],
      colors:[]
    },
    validationSchema,
    onSubmit: async(values) => {

        console.log(va);
        const product = { ...data };
        product.images = [
          product.image1,
          product.image2,
          product.image3,
          product.thumbnail,
        ];
        product.highlights = [
          product.highlight1,
          product.highlight2,
          product.highlight3,
          product.highlight4,
        ];
        product.rating = 0;
        if (product.colors) {
          product.colors = product.colors.map((color) =>
            colors.find((clr) => clr.id === color)
          );
        }
        if (product.sizes) {
          product.sizes = product.sizes.map((size) =>
            sizes.find((sz) => sz.id === size)
          );
        }

        delete product['image1'];
        delete product['image2'];
        delete product['image3'];
        product.price = +product.price;
        product.stock = +product.stock;
        product.discountPercentage = +product.discountPercentage;
        console.log(product);
        if (params.id) {
          product.id = params.id;
          product.rating = selectedProduct.rating || 0;
          dispatch(updateProductAsync(product));
          alert.success('Product Updated');

