import { RouterProvider, createBrowserRouter } from "react-router-dom";
import HomePage from "./pages/HomePage";
import PageNotFound from "./pages/404Page";
import ResetPasswordPage from "./pages/ResetPasswordPage";
import ForgotPasswordPage from "./pages/ForgotPasswordPage";
import UserProfilePage from "./pages/UserProfilePage";
import UserOrdersPage from "./pages/UserOrdersPage";
import OrderSuccessPage from "./pages/OrderSuccessPage";
import ProductList from "./components/product/ProductList";
import NavBar from "./components/navbar/NavBar";
import LoginPage from "./pages/LoginPage";
import SignupPage from "./pages/SignupPage";
import CartPage from "./pages/CartPage";
import CheckoutPage from "./pages/CheckoutPage";
import ProductDetailPage from "./pages/ProductDetailPage";
import AdminProductDetailPage from "./pages/AdminProductDetailPage";
import AdminProductFormPage from "./pages/AdminProductFormPage";
import AdminOrdersPage from "./pages/AdminOrdersPage.";
import StripeCheckoutPage from "./pages/StripeCheckoutPage";
import Logout from "./components/auth/Logout";
import AdminHome from "./pages/AdminHome";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { fetchLoggedInUserAsync } from './redux/userSlice';
import { fetchItemsByUserIdAsync } from './redux/cartSlice';
import {
  checkAuthAsync,
  selectLoggedInUser,
  selectUserChecked,
} from './features/auth/authSlice';

const router=createBrowserRouter([
  {path:'/',element:(<HomePage/>)},
  {path:'/admin',element:(<AdminHome/>)},
  {path:'/login',element:(<LoginPage/>)},
  {path:'/signup',element:(<SignupPage/>)},
  {path:'/cart',element:(<CartPage/>)},
  {path:'/checkout',element:(<CheckoutPage/>)},
  {path:'/product-detail/:_id',element:(<ProductDetailPage/>)},
  {path:'/admin/product-detail/:id',element:(<AdminProductDetailPage/>)},
  {path:'/admin/product-form',element:(<AdminProductFormPage/>)},
  {path:'/admin/orders',element:(<AdminOrdersPage/>)},
  {path:'/admin/product-form/edit/:id',element:(<AdminProductFormPage/>)},
  {path:'/order-success/:id',element:(<OrderSuccessPage/>)},
  {path:'/my-orders',element:(<UserOrdersPage/>)},
  {path:'/profile',element:(<UserProfilePage/>)},
  {path:'/stripe-checkout/',element:(<StripeCheckoutPage/>)},
  {path:'/logout',element:(<Logout/>)},
  {path:'/forgot-password',element:(<ForgotPasswordPage/>)},
  {path:'/reset-password',element:(<ResetPasswordPage/>)},
  {path:'*',element:(<PageNotFound/>)}
])

function App() {

  const dispatch = useDispatch();
  const user = useSelector((state) => state.auth.loggedInUserToken);
  const userChecked = useSelector((state) => state.auth.userChecked);

  useEffect(() => {
    dispatch(checkAuthAsync());
  }, [dispatch]);

  useEffect(() => {
    if (user) {
      dispatch(fetchItemsByUserIdAsync());
      // we can get req.user by token on backend so no need to give in front-end
      dispatch(fetchLoggedInUserAsync());
    }
  }, [dispatch, user]);
  return (
    <div className="">
       <RouterProvider router={router} />
    </div>
  );
}

export default App;
