import axios from "axios";
import CustomErrorToast from "../components/resuablecomponent/toaster/CustomErrorToast";
import { navigate } from "react-router-dom"; // Import navigate from 'react-router-dom'

const token = localStorage.getItem("token");

// Function to create Axios instance with interceptor
const createAxiosInstance = (navigate) => {
    const Config = axios.create({
        baseURL: `http://localhost:8080/`,
    });

    Config.interceptors.response.use(
        (response) => {
            return response;
        },
        (error) => {
            console.log(error?.response?.status, "error");
            if (error?.response?.status === 401) {
                // Navigate to the login page using the provided navigate function
                navigate("/login");
            }
            const updatedMessage = error?.response?.data?.message;
            CustomErrorToast(updatedMessage);
            return Promise.reject(error); // Reject the promise to propagate the error
        }
    );

    return Config;
};

const Config = createAxiosInstance(navigate);

export default Config;
